 :root:is([sizemode="maximized"], [sizemode="fullscreen"]) .titlebar-max {
     display: none !important;
 }

 .titlebar-buttonbox {
     margin-inline: 0 !important;
 }

 .titlebar-buttonbox-container {
     margin-bottom: 0px;
 }

 /*  Menubar  */
 /*  #toolbar-menubar[inactive]  *
 #main-window:has(#toolbar-menubar:not([inactive])) #toolbar-menubar {
     margin-bottom: -20px !important
 }

 #main-window:has(#toolbar-menubar:not([inactive])) #toolbar-menubar .titlebar-buttonbox-container {
     margin-top: -1px !important;
     height: 44px !important
 }

 #main-window:has(#toolbar-menubar:not([inactive])) #alltabs-button {
     margin-top: -24px !important;
     margin-bottom: 24.2px !important;
 }

 @media screen and (min-width:566px) {
     #main-window:has(#toolbar-menubar[autohide="false"]) #alltabs-button {
         margin: -25px 168px 25.2px 0 !important;
     }
 }

 #main-menubar {
     flex-wrap: wrap !important;
     max-width: calc(100vw - 226px);
 }

 @media screen and (max-width:565px) {
     #main-window:has(#toolbar-menubar:not([inactive])) #TabsToolbar-customization-target {
         margin-top: 20px;
     }

     #main-window:has(#toolbar-menubar:not([inactive])) #alltabs-button {
         height: 44px !important;
         position: absolute;
         top: 24px;
         right: 168px;
     }

     #main-window:has(#toolbar-menubar:not([inactive])) #toolbar-menubar {
         height: fit-content !important;
     }
 }

 #main-menubar {
     z-index: 1;
 }

 /*  Findbar hacks  */
 @media screen and (max-width:849px) {
     .findbar-container>hbox[anonid="findbar-textbox-wrapper"] {
         width: 100% !important
     }

     .findbar-textbox {
         width: -moz-available !important
     }

     .findbar-container {
         display: flex !important;
         flex-wrap: wrap !important;
         height: unset !important
     }

     findbar {
         height: 90px !important
     }
 }

 @media screen and (min-width:522px) and (max-width:541px) {
     .findbar-container>checkbox {
         margin-bottom: -12px !important
     }
 }

 @media screen and (max-width:521px) {
     findbar {
         height: 122px !important
     }
 }

 /*  Findbar size at larger widths  */
 @media screen and (min-width:850px) and (max-width:1324px) {
     .findbar-textbox {
         width: calc(64vw - 290px) !important
     }
 }

 @media screen and (min-width:1325px) {
     .findbar-textbox {
         width: 558px !important
     }

     /*  Tab preview  */
     #mainPopupSet #tab-preview-panel .tab-preview-thumbnail-container {
         border-radius: 8px;
         border: none !important
     }

     #mainPopupSet #tab-preview-panel .tab-preview-thumbnail-container canvas {
         border-radius: 8px;
     }

     #tab-preview-panel {
         --panel-border-radius: 8px !important
     }
 }

 #navigator-toolbox-background:-moz-window-inactive {
     filter: contrast(90%) !important;
 }

 /*  Fades window while not in focus */
 :root {
     --tabpanel-background-color: -moz-Dialog !important
 }

 /*  Removes flash before loading pages  */
 /* Source file https://github.com/MrOtherGuy/firefox-csshacks/tree/master/chrome/autohide_sidebar.css made available under Mozilla Public License v. 2.0
See the above repository for updates as well as full license text. */

 /* Makes black favicons more visible on dark background, contrast will be lowered though */
 .tab-icon-image {
     filter: invert(40%) contrast(250%) saturate(250%) !important;
 }

 /* Why 100vw? Tab closing requires width animation to end and "none" can't be animated */
 .tabbrowser-tab[fadein]:not([style^="max-width"]) {
     max-width: 100vw !important
 }

 /* Source file https://github.com/MrOtherGuy/firefox-csshacks/tree/master/chrome/integrated_searchbar_popup.css made available under Mozilla Public License v. 2.0
See the above repository for updates as well as full license text. */

 /* This makes the searchbar popup look integrated into the searchbar,
 * kinda like urlbar popup looks.
 * Go to about:config and enable color-mix with layout.css.color-mix.enabled
 * to make the popup border show properly, or make the border use other color.
 */

 #PopupSearchAutoComplete,
 #searchbar {
     /* text input is clipped if this is too large compared to font-size. */
     --uc-searchbar-border-radius: 6px;
 }

 #PopupSearchAutoComplete {
     --uc-inner-margin: 6px;
     --uc-searchbar-border-outer-radius: calc(var(--uc-searchbar-border-radius) + var(--uc-inner-margin));
     --arrowpanel-border-color: transparent !important;
     margin-top: calc(1px - var(--uc-searchbar-border-radius));
     outline: 2px solid var(--toolbar-field-focus-border-color) !important;
     outline-offset: -8px;
     -moz-window-shadow: none !important;
     margin-inline: calc(0px - var(--uc-inner-margin));
     padding-inline: var(--uc-inner-margin);
     margin-bottom: -6px;
     padding-bottom: 6px;
     filter: drop-shadow(0 0 6px rgba(0, 0, 0, 0.2));
     background: transparent !important;
     border-radius: 0 0 var(--uc-searchbar-border-outer-radius) var(--uc-searchbar-border-outer-radius) !important;
 }

 #searchbar:focus-within {
     border-radius: var(--uc-searchbar-border-radius) !important;
 }

 slot {
     border-radius: 0 0 var(--uc-searchbar-border-radius) var(--uc-searchbar-border-radius) !important;
 }

 #PopupSearchAutoComplete::before {
     height: 8px;
     background: var(--toolbar-field-focus-background-color);
     border-inline: 2px solid var(--toolbar-field-focus-border-color);
     display: flex;
     content: "";
     position: relative;
     z-index: 2;
 }

 /* Source file https://github.com/MrOtherGuy/firefox-csshacks/tree/master/chrome/centered_tab_label.css made available under Mozilla Public License v. 2.0
See the above repository for updates as well as full license text. */

 .tab-label-container {
     display: grid;
     align-content: center;
 }

 .tab-secondary-label {
     justify-content: center;
 }

 .tab-label,
 .tab-secondary-label {
     overflow: hidden
 }

 .tabbrowser-tab[selected]:not(:hover) .tab-label-container:not([labeldirection="rtl"]),
 #tabbrowser-tabs:not([closebuttons="activetab"]) .tabbrowser-tab:not(:hover, [pinned]) .tab-label-container:not([labeldirection="rtl"]) {
     margin-inline-end: 6px;
 }

 /* Show sidebar only when the cursor is over it  */
 /* The border controlling sidebar width will be removed so you'll need to modify these values to change width */

 #sidebar-box {
     --uc-sidebar-width: 5px !important;
     --uc-sidebar-hover-width: 359px !important;
     --uc-autohide-sidebar-delay: 600ms;
     /* Wait 0.6s before hiding sidebar */
     --uc-autohide-transition-duration: 115ms;
     --uc-autohide-transition-type: linear;
     --browser-area-z-index-sidebar: 3 !important;
     position: relative !important;
     min-width: var(--uc-sidebar-width) !important;
     width: var(--uc-sidebar-width) !important;
     max-width: var(--uc-sidebar-width) !important;
     z-index: var(--browser-area-z-index-sidebar, 3) !important;
 }

 #sidebar-box[positionend] {
     direction: rtl
 }

 #sidebar-box[positionend]>* {
     direction: ltr
 }

 #sidebar-box[positionend]:-moz-locale-dir(rtl) {
     direction: ltr
 }

 #sidebar-box[positionend]:-moz-locale-dir(rtl)>* {
     direction: rtl
 }

 #main-window[sizemode="fullscreen"] #sidebar-box {
     --uc-sidebar-width: 1px !important;
 }

 #sidebar-splitter {
     display: none !important;
 }

 #sidebar-header {
     overflow: hidden !important;
     color: var(--chrome-color, inherit) !important;
     padding-inline: 0 !important;
 }

 #sidebar-header::before,
 #sidebar-header::after {
     content: "";
     display: flex;
     padding-left: 8px;
 }

 #sidebar-header,
 #sidebar {
     transition: min-width var(--uc-autohide-transition-duration) var(--uc-autohide-transition-type) var(--uc-autohide-sidebar-delay) !important;
     min-width: var(--uc-sidebar-width) !important;
     will-change: min-width !important;
 }

 #sidebar-box:hover>#sidebar-header,
 #sidebar-box:hover>#sidebar {
     min-width: var(--uc-sidebar-hover-width) !important;
     transition-delay: 0ms !important;
 }

 .sidebar-panel {
     background-color: transparent !important;
     color: var(--newtab-text-primary-color) !important;
 }

 .sidebar-panel #search-box {
     -moz-appearance: -moz-mac-source-list !important;
     background: transparent !important;
     background-color: rgba(249, 249, 250, 0.1) !important;
     color: inherit !important;
 }

 /* Add sidebar divider and give it background */

 #sidebar,
 #sidebar-header {
     background-color: inherit !important;
     border-inline: 1px solid rgba(23, 23, 23, 0.584) !important;
     border-inline-width: 0px 1px !important;
 }

 #sidebar-box:not([positionend])> :-moz-locale-dir(rtl),
 #sidebar-box[positionend]>* {
     border-inline-width: 1px 0px !important;
 }

 /* Move statuspanel to the other side when sidebar is hovered so it doesn't get covered by sidebar */

 #sidebar-box:not([positionend]):hover~#appcontent #statuspanel {
     inset-inline: auto 0px !important;
 }

 #sidebar-box:not([positionend]):hover~#appcontent #statuspanel-label {
     margin-inline: 0px !important;
     border-left-style: solid !important;
 }
